package Loops;

public class Qn9 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
// Write a program to print sum of first five positive integer numbers.
int num=5;
int sum=0;

	for(byte i=1;i<=num;i++) {
		sum=sum+i;
		
	}
	System.out.println("sum="+sum);
	}
			
		
		
	}



